import "./Header.css";
import Logo from "../../assets/logo.png";
import Bars from "../../assets/bars.png";
import { useState } from "react";
import { Link } from "react-scroll";

const Header = () => {
  const mobile = window.innerWidth <= 768 ? true : false;
  const [menu, setMenu] = useState(false);

  return (
    <div className="header">
      <img src={Logo} alt="Logo" className="logo"></img>
      {menu === false && mobile === true ? (
        <div
          style={{ backgroundColor: "var(--appColor)", padding: "0.5rem" }}
          onClick={() => setMenu(true)}
        >
          <img
            src={Bars}
            alt=""
            style={{ width: "1.7rem", height: "1.2rem", borderRadius: "5px" }}
          />
        </div>
      ) : (
        <ul className="header-menu">
          <li onClick={() => setMenu(false)}>
            <Link>Home</Link>
          </li>
          <li onClick={() => setMenu(false)}>Programs</li>
          <li onClick={() => setMenu(false)}>Why us</li>
          <li onClick={() => setMenu(false)}>Plans</li>
          <li>
            <Link
              onClick={() => setMenu(false)}
              to="testimonials"
              spy={true}
              smooth={true}
            ></Link>
            Testimonials
          </li>
        </ul>
      )}
    </div>
  );
};

export default Header;
